) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `notices`
--

INSERT INTO `notices` (`id`, `title`, `descriptionSeo`, `keywordSeo`, `alt`, `lock`, `status`, `slug`, `image`, `author`, `create_at`, `content`, `description`, `views`) VALUES
(14, '2 LÍ DO BẠN NÊN THUÊ XE 7 CHỖ GIÁ RẺ TAXIGO', '', '', '2 LÍ DO BẠN NÊN THUÊ XE 7 CHỖ GIÁ RẺ TAXIGO', 0, 0, '2-li-do-ban-nen-thue-xe-7-cho-gia-re-taxigo', '/app/webroot/upload/images/ce7646a74c54cecf1c05442c71f02147_M.jpg', 'Vũ Văn Dương', '2018-01-16 16:28:56', '<p><em>TaxiGo l&agrave; một trong những đơn vị lớn nhất cung cấp dịch vụ cho thu&ecirc; xe chạy c&aacute;c tuyến s&acirc;n bay, đường d&agrave;i tr&ecirc;n thị trường H&agrave; Nội cũng như c&aacute;c tỉnh ph&iacute;a Bắc. Được biết đến với nhiều ưu điểm. H&atilde;y c&ugrave;ng điểm qua một số l&yacute; do nổi bật khiến bạn phải&nbsp;<strong>thu&ecirc; xe 7 chỗ gi&aacute; rẻ</strong>&nbsp;của TaxiGo bạn nh&eacute;!</em></p>\r\n\r\n<h2><strong>1. TaxiGo &ndash; Dịch vụ thu&ecirc; xe 7 chỗ gi&aacute; rẻ chất lượng</strong></h2>\r\n\r\n<p>Với phương ch&acirc;m đặt sự h&agrave;i l&ograve;ng v&agrave; gi&aacute; trị m&agrave; kh&aacute;ch h&agrave;ng sẽ nhận được l&ecirc;n h&agrave;ng đầu, TaxiGo đ&atilde; kh&ocirc;ng ngừng n&acirc;ng cấp v&agrave; đổi mới dịch vụ, lu&ocirc;n c&oacute; c&aacute;c ch&iacute;nh s&aacute;ch bổ sung c&aacute;c d&ograve;ng xe hiện đại, sang trọng, nội thất tiện nghi, đảm bảo sự thoải m&aacute;i khi di chuyển nhằm tạo sự tin tưởng v&agrave; đ&aacute;p ứng mọi nhu cầu thiết yếu của kh&aacute;ch h&agrave;ng. B&ecirc;n cạnh đ&oacute;, dịch vụ cho<a href=\"http://www.xetienchuyen.vn/tin-tuc/mach-ban-meo-di-taxi-7-cho-duong-dai-gia-re\">&nbsp;<strong>thu&ecirc; xe &ocirc; t&ocirc; 7 chỗ</strong></a>&nbsp;của TaxiGo tại H&agrave; Nội c&ograve;n c&oacute; đội ngũ l&aacute;i xe được tuyển chọn, lịch thiệp, chuy&ecirc;n nghiệp v&agrave; lu&ocirc;n lu&ocirc;n đặt tr&aacute;ch nhiệm an to&agrave;n của chuyến xe l&ecirc;n h&agrave;ng đầu.</p>\r\n\r\n<p><img alt=\"Lý do nên thuê xe 7 chỗ của TaxiGo\" src=\"http://www.xetienchuyen.vn/images/ly-do-nen-thue-xe-7-cho-gia-re-cua-taxigo-1.jpg\" style=\"height:356px; width:625px\" /></p>\r\n\r\n<h2><strong>2. Dịch vụ cho thu&ecirc; xe 7 chỗ gi&aacute; rẻ của TaxiGo</strong></h2>\r\n\r\n<p><strong>&nbsp;</strong></p>\r\n\r\n<p>TaxiGo hiện tại cung cấp&nbsp;dịch vụ cho<strong>&nbsp;thu&ecirc; xe&nbsp;7 chỗ c&oacute; người l&aacute;i</strong>&nbsp;đời mới từ 2012 của c&aacute;c h&atilde;ng từ b&igrave;nh d&acirc;n đến&nbsp;<a href=\"http://www.xetienchuyen.vn/tin-tuc/taxi-duong-dai-va-vo-van-cac-cau-chuyen-do-kho-do-cuoi\">hạng sang</a>&nbsp;nổi tiếng tr&ecirc;n thế giới t&ugrave;y theo nhu cầu đi lại của kh&aacute;ch h&agrave;ng. Bạn c&oacute; thể thấy c&aacute;c d&ograve;ng như&nbsp;Santafe, Innova Toyota, Fortuner Toyota,... ở dịch vụ của TaxiGo. Những xe n&agrave;y đều c&oacute; thể chạy c&aacute;c chuyến đi s&acirc;n bay hoặc đi tỉnh đường d&agrave;i khi c&oacute; kh&aacute;ch đặt.&nbsp;<strong>Gi&aacute; thu&ecirc; xe 7 chỗ c&oacute; người l&aacute;i</strong>&nbsp;t&ugrave;y v&agrave;o từng thời điểm sẽ kh&aacute;c nhau, t&ugrave;y thuộc v&agrave;o c&aacute;c d&ograve;ng xe kh&aacute;c nhau c&oacute; thể kh&aacute;c nhau (hoặc cũng c&oacute; thể thay đổi do chỉ định của kh&aacute;ch h&agrave;ng),...</p>\r\n\r\n<p><img alt=\"Lý do nên thuê xe 7 chỗ giá rẻ của TaxiGo\" src=\"http://www.xetienchuyen.vn/images/ly-do-nen-thue-xe-7-cho-gia-re-cua-taxigo-2.jpg\" style=\"width:625px\" /></p>\r\n\r\n<p>C&aacute;ch thức đặt xe ở đ&acirc;y ho&agrave;n to&agrave;n đơn giản, bạn c&oacute; thể gọi trực tiếp về tổng đ&agrave;i hoặc đặt xe tr&ecirc;n ứng dụng c&oacute; t&ecirc;n: TaxiGo. Th&ocirc;ng thường, mọi người thường&nbsp;<em>m&aacute;ch</em>&nbsp;nhau c&aacute;ch tải app, tạo t&agrave;i khoản v&agrave; đặt xe v&igrave; thường c&oacute; nhiều m&atilde; giảm gi&aacute; v&agrave; khuyến mại h&agrave;ng th&aacute;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh c&aacute;c d&ograve;ng xe 7 chỗ,&nbsp;<strong>TaxiGo</strong>&nbsp;cũng cung cấp một loạt những d&ograve;ng xe phục vụ việc đi c&ocirc;ng t&aacute;c, hội họp, tham quan, du lịch của h&agrave;nh kh&aacute;ch. Nếu bạn&nbsp;<strong>cần thu&ecirc; xe 7 chỗ c&oacute; t&agrave;i xế</strong>&nbsp;ri&ecirc;ng cho bạn c&ugrave;ng gia đ&igrave;nh hoặc bạn b&egrave;, h&atilde;y li&ecirc;n hệ&nbsp;<strong>1900 0370</strong>&nbsp;ngay h&ocirc;m nay để được hưởng những ưu đ&atilde;i tốt nhất bạn nh&eacute;!</p>\r\n', 'Nếu là một người thường xuyên phải di chuyển, nhất định bạn đừng bỏ qua bài viết Tổng hợp các dịch vụ di chuyển, taxi đường dài tốt nhất hiện nay sau đây để có được sự lựa chọn phù hợp cho riêng mình. ', 0),
(15, 'Đội giá taxi đường dài dịp tết 2018', '', '', '154 xe 7 chỗ đi nội bài', 0, 1, '154-xe-7-cho-di-noi-bai', '/app/webroot/upload/images/ce7646a74c54cecf1c05442c71f02147_M.jpg', 'Vũ Văn Dương', '2018-01-16 16:51:21', '<p><strong>Thu&ecirc; xe 7 chỗ đi s&acirc;n bay Nội B&agrave;i</strong>&nbsp;gi&aacute; tốt 220k. TaxiGo cung cấp dịch vụ&nbsp;<strong>thu&ecirc; xe 7 chỗ</strong>&nbsp;đi H&agrave; Nội - Nội B&agrave;i trọn g&oacute;i chỉ từ 220k. Qu&yacute; kh&aacute;ch vui l&ograve;ng đặt xe theo số điện thoại 1900 0370 hoặc 02422678666.</p>\r\n\r\n<p>So với mặt bằng chung c&aacute;c loại h&igrave;nh dịch vụ taxi s&acirc;n bay th&ocirc;ng thường, hiện tại TaxiGo đang trợ gi&aacute; tối đa cho cả h&agrave;nh kh&aacute;ch lẫn t&agrave;i xế. Qu&yacute; kh&aacute;ch c&oacute; thể đặt xe di chuyển cung đường H&agrave; Nội - Nội B&agrave;i v&agrave; ngược lại với gi&aacute; cực rẻ trọn g&oacute;i chỉ từ 160k. Đặc biệt,&nbsp;TaxiGo cung cấp dịch vụ&nbsp;<strong>thu&ecirc; xe 7 chỗ đi s&acirc;n bay Nội B&agrave;i</strong>trọn g&oacute;i chỉ từ 220k.</p>\r\n\r\n<p>T&ugrave;y v&agrave;o nhu cầu cụ thể m&agrave; qu&yacute; kh&aacute;ch c&oacute; thể đặt cho m&igrave;nh một chiếc xe 7 chỗ của TaxiGo để trải nghiệm dịch vụ tốt nhất với gi&aacute; th&agrave;nh rẻ nhất.</p>\r\n\r\n<p>C&aacute;c d&ograve;ng xe 7 chỗ m&agrave; TaxiGo thường sử dụng l&agrave; Innova, Fortuner, Sorento, Ford Everest, Mazda Cx5...Tất cả c&aacute;c xe đều l&agrave; xe mới, chất lượng cao, đời xe từ 2014 trở l&ecirc;n. Khi&nbsp;<strong>thu&ecirc; xe 7 chỗ đi s&acirc;n bay Nội B&agrave;i&nbsp;</strong>của TaxiGo, qu&yacute; kh&aacute;ch sẽ được đi xe ri&ecirc;ng, sang trọng đời mới với mức gi&aacute; rẻ nhất thị trường.</p>\r\n\r\n<p>Khi sử dụng c&aacute;c dịch vụ di chuyển H&agrave; Nội - Nội B&agrave;i kh&aacute;c, qu&yacute; kh&aacute;ch rất dễ gặp phải trường hợp c&aacute;c h&atilde;ng taxi kh&aacute;c để gi&aacute; chung khi thu&ecirc; xe 4-5 chỗ&nbsp;<strong>đi s&acirc;n bay Nội B&agrave;i&nbsp;</strong>l&agrave; 220k. Tuy nhi&ecirc;n với TaxiGo, 220k bạn đ&atilde; thoải m&aacute;i tận hưởng dịch vụ đẳng cấp khi&nbsp;<strong>thu&ecirc; xe 7 chỗ đi s&acirc;n bay Nội B&agrave;i</strong>. Đi đ&ocirc;ng vui hơn, tiết kiệm hơn.</p>\r\n\r\n<p>Bảng gi&aacute;&nbsp;<strong>thu&ecirc; xe 7 chỗ đi s&acirc;n bay Nội B&agrave;i&nbsp;</strong>theo c&aacute;c quận nội th&agrave;nh H&agrave; Nội:</p>\r\n\r\n<p><em>Ba Đ&igrave;nh - Nội B&agrave;i: 240k,</em></p>\r\n\r\n<p><em>Ho&agrave;n Kiếm&nbsp;<em>- Nội B&agrave;i:&nbsp;<em>240k</em></em>,</em></p>\r\n\r\n<p><em>T&acirc;y Hồ&nbsp;<em>- Nội B&agrave;i:&nbsp;<em>240k</em></em>,</em></p>\r\n\r\n<p><em>Long Bi&ecirc;n&nbsp;<em>- Nội B&agrave;i:&nbsp;<em>240k</em></em>,</em></p>\r\n\r\n<p><em>Cầu Giấy&nbsp;<em>- Nội B&agrave;i:&nbsp;<em>240k</em></em>,</em></p>\r\n\r\n<p><em>Đống Đa&nbsp;<em>- Nội B&agrave;i:&nbsp;<em>240k</em></em>,</em></p>\r\n\r\n<p><em>Hai B&agrave; Trưng&nbsp;<em><em>- Nội B&agrave;i:&nbsp;<em>240k</em></em>,</em></em></p>\r\n\r\n<p><em>Thanh Xu&acirc;n&nbsp;<em><em>- Nội B&agrave;i:&nbsp;<em>240k</em></em></em>,</em></p>\r\n\r\n<p><em>Ho&agrave;ng Mai&nbsp;<em><em>- Nội B&agrave;i:&nbsp;<em>270k</em></em></em>,</em></p>\r\n\r\n<p><em>H&agrave; Đ&ocirc;ng&nbsp;<em><em>- Nội B&agrave;i:&nbsp;<em>270k</em></em></em>,</em></p>\r\n\r\n<p><em>Bắc Từ Li&ecirc;m&nbsp;<em><em>- Nội B&agrave;i:&nbsp;<em>270k</em></em>,</em></em></p>\r\n\r\n<p><em>Nam Từ Li&ecirc;m&nbsp;<em><em>- Nội B&agrave;i:&nbsp;<em>270k</em></em>,</em></em></p>\r\n\r\n<p>Để được b&aacute;o gi&aacute; cụ thể v&agrave; đặt xe nhanh ch&oacute;ng,&nbsp;Qu&yacute; kh&aacute;ch vui l&ograve;ng đặt xe theo số điện thoại 1900 0370 hoặc 02422678666.</p>\r\n', 'Nếu là một người thường xuyên phải di chuyển, nhất định bạn đừng bỏ qua bài viết Tổng hợp các dịch vụ di chuyển, taxi đường dài tốt nhất hiện nay sau đây để có được sự lựa chọn phù hợp cho riêng mình. 1. Đi tàu hỏa Đường sắt Bắc…', 7),
(28, 'TAXI HÀ NỘI VỀ NAM ĐỊNH, XE RIÊNG GIÁ 680,000Đ - XETIENCHUYEN.VN', 'mo ta noi dung seo', 'keyword seo', '111-quan-an-vat-cua-gioi-tre-ha-thanh-phan1', 0, 0, '111-quan-an-vat-cua-gioi-tre-ha-thanh-phan1', '/app/webroot/upload/images/26907403_1782579765149613_5056404696770947427_n.jpg', 'Vũ Dương', '2018-01-29 08:58:46', 'ABC ABC', 'mo ta ngan', 3),
(31, 'ĐIỂM DU LỊCH BIỂN THÚ VỊ CHO MÙA HÈ 20161234324234', '', '', '2-dia-m-du-la-ch-tha-va-cho-ma-a-ha-na-y', 0, 0, '2-dia-m-du-la-ch-tha-va-cho-ma-a-ha-na-y', '/app/webroot/upload/images/2-dia-m-du-la-ch-tha-va-cho-ma-a-ha-na-y-7.jpg', '', '2018-01-29 17:07:35', 'M&ugrave;a h&egrave; thời tiết Miền bắc thường kh&aacute; oi ả, ngột ngạt. &nbsp;Cuộc sống hiện đại, th&oacute;i quen của nhiều người l&agrave; lựa chọn cho m&igrave;nh những chuyến đi chơi, đi du lịch c&ugrave;ng gia đ&igrave;nh, bạn b&egrave;, người th&acirc;n để tự thưởng cho m&igrave;nh v&agrave; gia đ&igrave;nh sau một năm l&agrave;m việc vất vả. http://duadonnoibai.vn/ &nbsp;thấu hiểu v&agrave; lu&ocirc;n mong muốn đồng h&agrave;nh, g&oacute;p một phần gi&aacute; trị để r&uacute;t ngắn chặng h&agrave;nh tr&igrave;nh của c&aacute;c bạn.<br />\r\n<br />\r\n&nbsp;Đi biển l&agrave; một gợi &yacute; cho những ai muốn thư gi&atilde;n v&agrave; c&oacute; thời gian th&uacute; vị b&ecirc;n gia đ&igrave;nh, người th&acirc;n trong m&ugrave;a h&egrave; oi ả. Những địa điểm biển đẹp v&agrave; th&uacute; vị được http://duadonnoibai.vn/ &nbsp;tổng hợp nhằm gi&uacute;p c&aacute;c bạn c&oacute; những lựa chọn ph&ugrave; hợp cho m&ugrave;a h&egrave; 2016 n&agrave;y nh&eacute;:\r\n<h2><strong>1. Phan Thiết &ndash; thi&ecirc;n đường của c&aacute;t, nắng v&agrave; gi&oacute;</strong></h2>\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n<br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\nKh&ocirc;ng chỉ hấp dẫn bởi những b&atilde;i biển hoang sơ, Mũi N&eacute;, Phan Thiết c&ograve;n thu h&uacute;t du kh&aacute;ch bởi đồi c&aacute;t v&agrave;ng rực, trải d&agrave;i m&ecirc;nh m&ocirc;ng. Ngo&agrave;i vẻ đẹp mi&ecirc;n man, quyến rũ lạ l&ugrave;ng, đồi c&aacute;t c&ograve;n l&agrave; điểm vui chơi l&yacute; tưởng cho c&aacute;c bạn trẻ với c&aacute;c tr&ograve; trượt c&aacute;t, đi m&ocirc; t&ocirc; tr&ecirc;n c&aacute;t v&agrave; thả diều v&ocirc; c&ugrave;ng l&yacute; th&uacute;. Một chuyến du lịch đến Phan Thiết ng&agrave;y h&egrave;, tha hồ vẫy v&ugrave;ng tắm m&aacute;t v&agrave; nhất l&agrave; được thỏa th&iacute;ch chơi đ&ugrave;a, chụp ảnh lưu niệm tr&ecirc;n đồi c&aacute;t m&ecirc;nh m&ocirc;ng sẽ l&agrave; m&oacute;n qu&agrave; tuyệt vời cho c&aacute;c bạn trẻ sau một năm học tập.<br />\r\n<br />\r\n&nbsp;\r\n<h2>2. Thoải m&aacute;i vui h&egrave; tr&ecirc;n biển Mỹ Kh&ecirc;</h2>\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n<br />\r\n<br />\r\nĐược mẹ thi&ecirc;n nhi&ecirc;n ưu &aacute;i ban tặng bờ c&aacute;t phẳng, trải d&agrave;i v&agrave; rộng, s&oacute;ng biển hiền h&ograve;a, nước ấm quanh năm v&agrave; b&atilde;i biển được chiếu s&aacute;ng suốt cả đ&ecirc;m, Mỹ Kh&ecirc; l&agrave; điểm đến v&ocirc; c&ugrave;ng an to&agrave;n, ph&ugrave; hợp du kh&aacute;ch mọi lứa tuổi. Tắm biển Mỹ Kh&ecirc;, c&aacute;c bậc phụ huynh c&oacute; thể an l&ograve;ng cho con em m&igrave;nh tha hồ vẫy v&ugrave;ng nơi biển ấm m&agrave; kh&ocirc;ng sợ cảm lạnh. C&aacute;c cụ gi&agrave; sẽ được thỏa th&iacute;ch ng&acirc;m m&igrave;nh trong l&agrave;n nước trong xanh m&agrave; kh&ocirc;ng phải lo s&oacute;ng to, sức yếu. Đến Mỹ Kh&ecirc;, du kh&aacute;ch c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m, thoải m&aacute;i m&agrave; tận hưởng một kỳ nghỉ h&egrave; trọn vẹn nhất b&ecirc;n gia đ&igrave;nh.<br />\r\n&nbsp;\r\n<h2><strong>3. Đến Ph&uacute; Y&ecirc;n, kh&aacute;m ph&aacute; thi&ecirc;n nhi&ecirc;n kỳ th&uacute;</strong></h2>\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\nBiển Ph&uacute; Y&ecirc;n với thi&ecirc;n nhi&ecirc;n kỳ th&uacute; cũng l&agrave; một điểm đến v&ocirc; c&ugrave;ng th&uacute; vị cho du kh&aacute;ch trong m&ugrave;a h&egrave; năm nay. Ngo&agrave;i Vịnh Xu&acirc;n Đ&agrave;i, Vịnh Vũng R&ocirc; với những b&atilde;i tắm n&ecirc;n thơ, Ph&uacute; Y&ecirc;n c&ograve;n sở hữu G&agrave;nh Đ&aacute; Dĩa v&ocirc; c&ugrave;ng độc đ&aacute;o, được xem l&agrave; thắng cảnh độc nhất v&ocirc; nhị tr&ecirc;n to&agrave;n c&otilde;i Việt Nam. Thử một lần kh&aacute;m ph&aacute; G&agrave;nh Đ&atilde; Dĩa, đi ch&acirc;n trần dạo bước tr&ecirc;n những tầng đ&aacute; tr&ocirc;ng giống như chồng đĩa nh&ocirc; ra biển, cảm nhận vị mặn của biển, c&aacute;i r&aacute;t nhẹ của gi&oacute;, tiếng ồn &agrave;o của từng đợt s&oacute;ng đập mạnh v&agrave;o bờ&hellip;sẽ để lại ấn tượng kh&oacute; phai trong l&ograve;ng du kh&aacute;ch.<br />\r\n<br />\r\n&nbsp;\r\n<h2><strong>4. Biển Nha Trang rực rỡ ng&agrave;y h&egrave;</strong></h2>\r\n<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\nL&agrave; một th&agrave;nh phố biển vốn xinh đẹp, vui tươi, Nha Trang trở n&ecirc;n tr&agrave;n đầy sức sống, rực rỡ hơn bao giờ hết dưới nắng h&egrave;. Một lần đến Nha Trang, du kh&aacute;ch sẽ được thỏa sức vẫy v&ugrave;ng trong biển ấm với c&aacute;c tr&ograve; thể thao s&ocirc;i động, đi t&agrave;u đ&aacute;y k&iacute;n, lặn biển ngắm san h&ocirc; hay tận hưởng cảm gi&aacute;c lơ lửng giữa biển trời với chuyến c&aacute;p treo vượt đại dương để đến khu giải tr&iacute; Vinpearl Land s&ocirc;i động. C&oacute; đến Nha Trang v&agrave;o những ng&agrave;y h&egrave; đầy nắng, du kh&aacute;ch mới cảm nhận hết sự tươi vui, tuyệt vời của th&agrave;nh phố biển thơ mộng n&agrave;y.<br />\r\n&nbsp;\r\n<h2><strong>5. Biển Cửa Đại, n&eacute;t duy&ecirc;n ngầm của Phố Cổ</strong></h2>\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n<br />\r\n<br />\r\n&nbsp;<br />\r\nCũng l&agrave; biển xanh, c&aacute;t trắng, nắng v&agrave;ng v&agrave; những resort cao cấp, kh&aacute;ch sạn hiện đại&hellip;nhưng biển Cửa Đại, Hội An lại sở hữu n&eacute;t đẹp rất ri&ecirc;ng, y&ecirc;n b&igrave;nh v&agrave; trầm tĩnh như ch&iacute;nh c&aacute;i hồn của phố cổ. Đến Cửa Đại, du kh&aacute;ch c&oacute; thể tha hồ ng&acirc;m m&igrave;nh trong l&agrave;n nước trong vắt, để từng cơn s&oacute;ng vỗ về, cuốn đi bao mệt nhọc, nằm d&agrave;i h&agrave;ng giờ tr&ecirc;n bờ c&aacute;t trắng phau, nghe tiếng gi&oacute; vi vu như điệu nhạc hay rong ruổi dạo chơi dưới h&agrave;ng dừa nghi&ecirc;ng m&igrave;nh trong nắng&hellip;Kh&ocirc;ng gian n&ecirc;n thơ, tĩnh lặng nơi đ&acirc;y sẽ x&oacute;a đi bao ưu phiền trong l&ograve;ng du kh&aacute;ch, đem đến kỳ nghỉ h&egrave; b&igrave;nh y&ecirc;n v&agrave; thư th&aacute;i đến tuyệt vời.<br />\r\n&nbsp;\r\n<h2><strong>6. M&ugrave;a h&egrave; tr&ecirc;n th&agrave;nh phố Vũng T&agrave;u</strong></h2>\r\n<br />\r\nChỉ c&aacute;ch TP.HCM 120km, Vũng T&agrave;u l&agrave; điểm đến l&yacute; tưởng cho chuyến du lịch ngắn ng&agrave;y, ph&ugrave; hợp với những du kh&aacute;ch Phương Nam c&oacute; nhịp sống bận rộn, muốn tranh thủ ch&uacute;t thời gian ng&agrave;y h&egrave; vui chơi b&ecirc;n gia đ&igrave;nh. Kh&ocirc;ng qu&aacute; y&ecirc;u kiều với c&aacute;t trắng, biển xanh như Mỹ Kh&ecirc;, Cửa Đại hay s&ocirc;i động như Nha Trang&hellip;biển Vũng T&agrave;u c&oacute; phần b&igrave;nh dị, mộc mạc nhưng lại đủ đầy mọi thứ từ b&atilde;i tắm lặng s&oacute;ng, khu giải tr&iacute; Hồ M&acirc;y, những cuộc đua ch&oacute; vui nhộn đến những cung đường l&atilde;ng mạn c&ugrave;ng chiếc xe đạp đ&ocirc;i&hellip;để đem đến du kh&aacute;ch một kỳ nghỉ th&uacute; vị. Dừng ch&acirc;n ở Vũng T&agrave;u d&ugrave; chỉ vỏn vẹn v&agrave;i ng&agrave;y nhưng chắc chắn du kh&aacute;ch sẽ t&igrave;m thấy cả m&ugrave;a h&egrave; tuyệt vời đều ở nơi đ&acirc;y.<br />\r\n&nbsp;\r\n<h2><strong>7. Trải nghiệm m&ugrave;a h&egrave; nơi Đảo Ngọc</strong></h2>\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\nNhắc đến Ph&uacute; Quốc, chắc hẳn nhiều người nghĩ ngay đến những b&atilde;i biển hoang sơ, rừng xanh mướt, khu di t&iacute;ch lịch sử ghi dấu một thời đấu tranh gian khổ m&agrave; h&agrave;o h&ugrave;ng của d&acirc;n tộc. Nhưng Ph&uacute; Quốc đ&acirc;u chỉ c&oacute; thế. Chuyến du lịch Ph&uacute; Quốc c&ograve;n l&agrave; dịp để du kh&aacute;ch trải nghiệm cuộc sống mộc mạc nhưng v&ocirc; c&ugrave;ng th&uacute; vị của người d&acirc;n xứ đảo khi gh&eacute; thăm l&agrave;ng ch&agrave;i, cơ sở sản xuất ngọc trai, nh&agrave; th&ugrave;ng nước mắm, hay xưởng sản xuất rượu sim v&agrave; vườn hồ ti&ecirc;u xanh ngắt&hellip;Nếu du kh&aacute;ch muốn t&igrave;m một điểm du lịch h&egrave; để nghỉ ngơi, thư gi&atilde;n, h&ograve;a m&igrave;nh với thi&ecirc;n nhi&ecirc;n, được biết th&ecirc;m nhiều điều hay, bổ &iacute;ch về cuộc sống người d&acirc;n miền biển th&igrave; Đảo Ngọc Ph&uacute; Quốc sẽ l&agrave; một điểm đến ho&agrave;n hảo.<br />\r\n<br />\r\n&nbsp;\r\n<h2><strong>8. S&ocirc;i động biển SihanoukVille</strong></h2>\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n<br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\nMang trong m&igrave;nh vẻ đẹp b&igrave;nh y&ecirc;n, thơ mộng v&agrave;o buổi sớm mai, nhưng khi đ&ecirc;m xuống, biển SihanoukVille, Campuchia lại rực rỡ v&agrave; n&aacute;o nhiệt hơn bao giờ hết với ph&aacute;o hoa s&aacute;ng rực bầu trời, tiếng nhạc xập x&igrave;nh từ c&aacute;c qu&aacute;n bar ven biển hay những m&agrave;n m&uacute;a lửa đi&ecirc;u luyện, trở th&agrave;nh một chốn vui chơi đ&iacute;ch thực v&agrave; rất &ldquo;T&acirc;y&rdquo;. Một lần đến SihanoukVille để thỏa sức h&ograve;a v&agrave;o những tr&ograve; giải tr&iacute; vui nhộn hay chỉ đơn giản ngồi thưởng thức bia Angkor m&aacute;t lạnh dưới bầu trời đ&ecirc;m chốc chốc s&aacute;ng bừng rực rỡ khi ngọn đ&egrave;n trời lướt qua hay những đ&oacute;a ph&aacute;o hoa rực lửa nổ đ&ugrave;ng đo&agrave;ng, lấn &aacute;t cả m&agrave;n đ&ecirc;m huyền ảo&hellip;Tất cả những điều đ&oacute; sẽ mang đến du kh&aacute;ch những trải nghiệm v&ocirc; c&ugrave;ng độc đ&aacute;o v&agrave; mới lạ.<br />\r\n<br />\r\n&nbsp;\r\n<h2><strong>9. Maldives, nơi chỉ c&aacute;ch thi&ecirc;n đường một bước ch&acirc;n</strong></h2>\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n<br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\nVới những ốc đảo l&atilde;ng mạn, tr&agrave;n ngập nắng ấm của miền nhiệt đới, b&atilde;i c&aacute;t trắng tinh, nước biển xanh m&agrave;u ngọc b&iacute;ch đẹp đến m&ecirc; hồn, thật kh&ocirc;ng ngoa khi n&oacute;i Maldives l&agrave; thi&ecirc;n đường nơi hạ giới. Chẳng c&oacute; những tr&ograve; giải tr&iacute; s&ocirc;i động, nơi đ&acirc;y chỉ c&oacute; biển trời bao la như h&ograve;a l&agrave;m một, v&agrave; những khu nghỉ dưỡng tiện nghi để du kh&aacute;ch thỏa sức vẫy v&ugrave;ng, lặn biển ngắm san h&ocirc; hay chỉ đơn giản l&agrave; mắc chiếc v&otilde;ng dưới b&oacute;ng dừa đong đưa ho&agrave;i kh&ocirc;ng ch&aacute;n&hellip;Maldives sẽ đưa du kh&aacute;ch sẽ lạc v&agrave;o một thế giới ho&agrave;n to&agrave;n kh&aacute;c, chẳng c&oacute; một ch&uacute;t tạp &acirc;m n&agrave;o từ cuộc sống, chẳng c&ograve;n những lo toan phiền muộn, chỉ c&oacute; tiếng gi&oacute;, tiếng s&oacute;ng như lời thủ thỉ t&acirc;m t&igrave;nh ngọt ng&agrave;o từ biển, dịu &ecirc;m v&agrave; thanh b&igrave;nh qu&aacute; đỗi. C&oacute; lẽ v&igrave; vậy m&agrave; Maldives trở th&agrave;nh điểm đến ưu th&iacute;ch của giới thượng lưu, những ng&ocirc;i sao, nhiều doanh nh&acirc;n nổi tiếng.<br />\r\n<br />\r\n&nbsp;\r\n<h2><strong>10. Đến Manukan, vẫy v&ugrave;ng c&ugrave;ng đ&agrave;n c&aacute;</strong></h2>\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n<br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\nĐược v&iacute; l&agrave; thi&ecirc;n đường của c&aacute;, san h&ocirc;, đảo Manukan, Malaysia sẽ đem đến du kh&aacute;ch những trải nghiệm tuyệt vời về thế giới đại dương s&ocirc;i động. Với Manukan, thậm ch&iacute; chẳng cần lặn biển m&agrave; chỉ cần đi bộ dọc theo chiếc cầu gỗ hay ng&acirc;m m&igrave;nh trong l&agrave;n nước trong xanh, du kh&aacute;ch đ&atilde; c&oacute; thể tha hồ vẫy v&ugrave;ng, bơi lội với mu&ocirc;n ng&agrave;n lo&agrave;i c&aacute; đủ h&igrave;nh dạng, m&ugrave;a sắc, k&iacute;ch thước kh&aacute;c nhau&hellip;. Chỉ bấy nhi&ecirc;u đ&oacute; th&ocirc;i, Manukan cũng đủ l&agrave; điếm đến l&yacute; tưởng mang lại kỳ nghỉ h&egrave; tươi vui v&agrave; bổ &iacute;ch cho du kh&aacute;ch, đặc biệt l&agrave; c&aacute;c bạn nhỏ.<br />\r\n<br />\r\n&nbsp;\r\n<h2><strong>11. Độc đ&aacute;o biển Đỏ, Hy Lạp</strong></h2>\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n<br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\nC&oacute; bao giờ bạn tưởng tượng rằng m&igrave;nh sẽ được vui chơi, nằm d&agrave;i tắm nắng tr&ecirc;n bờ biển đỏ rực? H&igrave;nh th&agrave;nh từ đợt phun tr&agrave;o n&uacute;i lửa c&aacute;ch đ&acirc;y h&agrave;ng triệu năm, Biển Đỏ, Hy Lạp sở hữu bờ c&aacute;t trải d&agrave;i, đỏ như rượu vang l&agrave; một nơi kỳ lạ v&agrave; tuyệt vời như thế. V&agrave; d&ugrave; b&atilde;i biển c&oacute; m&agrave;u g&igrave; th&igrave; nước biển nơi đ&acirc;y vẫn lu&ocirc;n một m&agrave;u xanh ngắt, trong vắt như ngọc Saphia. M&ugrave;a h&egrave; đến, dưới &aacute;nh nắng ch&oacute;i chang của miền Địa Trung Hải, biển v&agrave; c&aacute;t nơi đ&acirc;y c&agrave;ng rực rỡ hơn bao giờ hết, tạo n&ecirc;n một bức tranh vừa độc đ&aacute;o, vừa quyến rũ lạ l&ugrave;ng &hellip; sẽ h&uacute;t hồn bất cứ ai lạc bước đến nơi n&agrave;y.<br />\r\n<br />\r\n&nbsp;\r\n<h2><strong>12. Đảo Bali, vi&ecirc;n ngọc qu&yacute; của đất nước Indonesia</strong><br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\n<br />\r\n<br />\r\n&nbsp;</h2>\r\n\r\n<p><br />\r\nBali &ndash; Indonesia kh&ocirc;ng những thu h&uacute;t du kh&aacute;ch bởi những b&atilde;i biển n&ecirc;n thơ tuyệt đẹp m&agrave; c&ograve;n l&agrave; n&eacute;t văn h&oacute;a độc đ&aacute;o của người d&acirc;n xứ đảo, ruộng bậc thang tầng tầng, lớp lớp, rừng gi&agrave; nhiệt đới v&agrave; hệ thống n&uacute;i lửa đầy b&iacute; ẩn&hellip; Với tất cả những điều đ&oacute;, Bali c&oacute; đầy đủ mọi loại h&igrave;nh du lịch, đ&aacute;p ứng nhu cầu kh&aacute;c nhau của du kh&aacute;ch từ du lịch nghỉ dưỡng hay những chuyến phi&ecirc;u lưu &ldquo;l&ecirc;n rừng xuống biển&rdquo; đến những h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; thi&ecirc;n nhi&ecirc;n v&agrave; n&eacute;t văn h&oacute;a độc đ&aacute;o của con người nơi đ&acirc;y. Bali sẽ l&agrave; một điểm đến kh&ocirc;ng bao giờ nh&agrave;m ch&aacute;n, lu&ocirc;n chứa đựng những điều kỳ th&uacute;, bất ngờ đợi chờ du kh&aacute;ch kh&aacute;m ph&aacute;.</p>\r\n', '', 5),
(34, 'TAXI HÀ NỘI ĐI HỒ ĐẠI LẢI TRỌN GÓI CHỈ 450.000Đ - TAXIGO.VN', '', '', '80-taxi-ha-noi-di-ho-dai-lai-gia-re', 0, 0, '80-taxi-ha-noi-di-ho-dai-lai-gia-re', '/app/webroot/upload/images/khu-du-lich-dai-lai-3.jpg', '', '2018-02-01 08:47:24', '<h1><em><strong>Taxi H&agrave; Nội đi Hồ Đại Lải gi&aacute; rẻ chỉ: 450.000vnđ - li&ecirc;n hệ: 0422.678.666 - Taxigo.vn</strong></em></h1>\r\n\r\n<p><strong><a href=\"http://www.taxigo.vn/tin-tuc/item/57-taxigo-taxi-gia-ra-ha-nai-i-pha-la-ha-nam-cha-540-000\">&nbsp;&nbsp;</a><a href=\"http://www.xetienchuyen.vn/taxi-duong-dai/taxi-ha-noi-di-dai-lai\">Taxi H&agrave; Nội đi Hồ Đại Lải&nbsp;</a><em>&nbsp;</em></strong>chưa bao giờ rẻ đến thế, đi xe ri&ecirc;ng, sang trọng trọn g&oacute;i chỉ 450.000đ</p>\r\n\r\n<p>Đặt xe ngay để l&agrave; kh&aacute;ch h&agrave;ng may mắn nhất, được&nbsp;<strong><em>miễn ph&iacute; cầu đường</em></strong>&nbsp;nếu bạn đặt xe trước 6 tiếng&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; Xe 7 chỗ gi&aacute; cộng th&ecirc;m 20%</p>\r\n\r\n<p><em>Đặc biệt:&nbsp;Trong trường hợp ch&uacute;ng t&ocirc;i kết nối được xe tiện chuyến, qu&yacute; kh&aacute;ch sẽ được ho&agrave;n trả lại ph&iacute; cầu đường &ndash; để nhận được ưu đ&atilde;i n&agrave;y qu&yacute; kh&aacute;ch n&ecirc;n đặt xe trước 12 giờ để ch&uacute;ng t&ocirc;i kết nối với c&aacute;c l&aacute;i xe.</em></p>\r\n\r\n<h2><strong>V&Agrave;I N&Eacute;T VỀ HỒ ĐẠI LẢI</strong></h2>\r\n\r\n<p>Đại Lải l&agrave; một trong những điểm du lịch l&yacute; tưởng, vừa gần H&agrave; Nội, kh&ocirc;ng kh&iacute; trong l&agrave;nh. Thời gian l&yacute; tưởng nhất để l&ecirc;n Đại Lải l&agrave; từ th&aacute;ng 4 đến th&aacute;ng 9. Đ&acirc;y l&agrave; khoảng thời gian kh&aacute; m&aacute;t mẻ</p>\r\n\r\n<p>Đại Lải c&oacute; nắng v&agrave;o ban ng&agrave;y, se se lạnh v&agrave;o ban đ&ecirc;m, th&iacute;ch hợp để bạn hẹn h&ograve; đ&ocirc;i lứa hoặc đi nghỉ dưỡng với gia đ&igrave;nh.Với chu vi khoảng 10km2, Đại Lải được nhiều bạn trẻ t&igrave;m đến để kh&aacute;m ph&aacute;, vui chơi. Tại đ&acirc;y c&oacute; c&aacute;c dịch vụ đa dạng như bơi thuyền, đạp vịt... ở giữa hồ c&ograve;n c&oacute; đảo C&ograve; (hay c&ograve;n gọi l&agrave; đảo Ngọc) th&iacute;ch hợp để du lịch m&ugrave;a h&egrave;.</p>\r\n\r\n<p>Nếu bạn muốn sử dụng tất cả kỳ nghỉ d&agrave;i của m&igrave;nh cho chuyến du lịch xa nh&agrave;, c&oacute; thể chọn tour Đại Lải - Tam Đảo - T&acirc;y Thi&ecirc;n. Đ&acirc;y đều l&agrave; những điểm du lịch nổi tiếng của Vĩnh Ph&uacute;c. Cảnh đẹp, m&oacute;n ăn ngon, kh&iacute; hậu trong l&agrave;nh, m&aacute;t mẻ... rất th&iacute;ch hợp để bạn v&agrave; gia đ&igrave;nh đến nghỉ dưỡng</p>\r\n\r\n<h2><strong>C&Aacute;CH T&Iacute;NH GI&Aacute; V&Agrave; PH&Aacute;T SINH</strong></h2>\r\n\r\n<table border=\"3\" cellspacing=\"3\" style=\"width:536px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Taxi H&agrave; Nội - hồ Đại Lải&nbsp;</td>\r\n			<td>\r\n			<p>Gi&aacute; Tiền</p>\r\n\r\n			<p>(Xe Ri&ecirc;ng, Trọn G&oacute;i)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xe 4 Chỗ</td>\r\n			<td>400.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xe 5 Chỗ</td>\r\n			<td>450.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xe 7 Chỗ</td>\r\n			<td>550.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xe 16 Chỗ</td>\r\n			<td>750.000đ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n&nbsp;\r\n\r\n<p><br />\r\n<strong>Ch&uacute; &yacute;:</strong></p>\r\n\r\n<p>- Bảng gi&aacute; tr&ecirc;n chưa bao gồm ph&iacute; cầu đường.</p>\r\n\r\n<p>- Gi&aacute; tr&ecirc;n sẽ được&nbsp;<strong>miễn ph&iacute;</strong>&nbsp;&quot;ph&iacute; cầu đường&quot; nếu qu&yacute; kh&aacute;ch khởi h&agrave;nh từ 6h -18h v&agrave; đặt xe trước 6 tiếng.&nbsp;</p>\r\n\r\n<p>- Xe 4 chỗ: 8000đ/km ph&aacute;t sinh.</p>\r\n\r\n<p>- Xe 5 chỗ: 9000đ/km ph&aacute;t sinh.</p>\r\n\r\n<p>- Xe 7 chỗ: 11.000đ/km ph&aacute;t sinh.&nbsp;&nbsp;</p>\r\n\r\n<h2><em>&nbsp;</em><strong>TAXIGO CAM KẾT</strong></h2>\r\n\r\n<p><em>-</em>&nbsp;Ch&uacute;ng t&ocirc;i đặt chất lượng dịch vụ l&ecirc;n h&agrave;ng đầu, với 100% d&ograve;ng xe sedan sang trọng, xe mới, sạch sẽ, l&aacute;i xe th&acirc;n thiện, lịch sự v&agrave; đ&uacute;ng giờ.</p>\r\n\r\n<p>- Ch&uacute;ng t&ocirc;i sẽ kết nối kh&aacute;ch h&agrave;ng với xe chạy rỗng chiều về, để kết hợp th&agrave;nh chuyến đi gi&aacute; rẻ với d&ograve;ng xe sang trọng, với gi&aacute; giảm tới 50%</p>\r\n\r\n<p>- Ch&uacute;ng t&ocirc;i sẽ ho&agrave;n lại 10%-200% tiền cho qu&yacute; kh&aacute;ch khi kh&aacute;ch h&agrave;ng kh&ocirc;ng h&agrave;i l&ograve;ng về dịch vụ của ch&uacute;ng t&ocirc;i</p>\r\n\r\n<p>- Ch&uacute;ng t&ocirc;i sẽ ho&agrave;n lại một phần tiền cho qu&yacute; kh&aacute;ch khi ch&uacute;ng t&ocirc;i kết hợp được xe tiện chuyến.</p>\r\n\r\n<p>- Ch&uacute;ng t&ocirc;i lu&ocirc;n cố gắng mang lại nhiều gi&aacute; trị nhất cho kh&aacute;ch h&agrave;ng, hỗ trợ kh&aacute;ch h&agrave;ng v&agrave; l&aacute;i xe trong suốt h&agrave;nh tr&igrave;nh chuyến đi</p>\r\n\r\n<p>CH&Uacute;C BẠN LU&Ocirc;N T&Igrave;M ĐƯỢC XE GI&Aacute; RẺ TẠI:<em><a href=\"http://www.taxigo.vn/\">&nbsp;taxigo.vn</a></em></p>\r\n', '', 0),
(35, 'TAXI HÀ NỘI ĐI VINH CHỈ 2.400.000Đ - TAXIGO.VN', '', '', '79-tax-ha-noi-di-vinh-gia-re', 0, 0, '79-tax-ha-noi-di-vinh-gia-re', '/app/webroot/upload/images/vinh.jpg', '', '2018-02-01 08:49:12', '<h1><em><strong>Taxi H&agrave; Nội đi Vinh gi&aacute; rẻ chỉ: 2.400.000vnđ &nbsp;- taxigo.vn - li&ecirc;n hệ: 0422.678.666</strong></em></h1>\r\n\r\n<p>&nbsp;&nbsp;<strong><a href=\"http://www.taxigo.vn/tin-tuc/item/57-taxigo-taxi-gia-ra-ha-nai-i-pha-la-ha-nam-cha-540-000\">&nbsp;&nbsp;</a><a href=\"http://www.xetienchuyen.vn/taxi-duong-dai/taxi-ha-noi-vinh-gia-re\">Taxi H&agrave; Nội đi Vinh&nbsp;</a></strong>chưa bao giờ rẻ đến thế, đi xe ri&ecirc;ng, sang trọng trọn g&oacute;i chỉ 2.400.000đ</p>\r\n\r\n<p>&nbsp;&nbsp; Đặt xe ngay để l&agrave; kh&aacute;ch h&agrave;ng may mắn nhất,&nbsp;<strong><em>miễn ph&iacute; cầu đường</em></strong>&nbsp;nếu bạn đặt xe trước 6 tiếng&nbsp;</p>\r\n\r\n<p><em>Đặc biệt:&nbsp;Trong trường hợp ch&uacute;ng t&ocirc;i kết nối được xe tiện chuyến, qu&yacute; kh&aacute;ch sẽ được ho&agrave;n trả lại ph&iacute; cầu đường &ndash; để nhận được ưu đ&atilde;i n&agrave;y qu&yacute; kh&aacute;ch n&ecirc;n đặt xe trước 12 giờ để ch&uacute;ng t&ocirc;i kết nối với c&aacute;c l&aacute;i xe.</em></p>\r\n\r\n<h2><strong>V&Agrave;I N&Eacute;T VỀ VINH</strong></h2>\r\n\r\n<p>Th&agrave;nh phố Vinh l&agrave; trung t&acirc;m kinh tế, ch&iacute;nh trị v&agrave; l&agrave; nơi hội tụ tiềm lực nh&acirc;n văn, thi&ecirc;n nhi&ecirc;n cũng như tinh hoa xứ Nghệ. Th&agrave;nh phố Vinh c&aacute;ch thủ đ&ocirc; H&agrave; Nội 291 km,</p>\r\n\r\n<p>Đến Vinh du kh&aacute;ch c&oacute; thể thưởng thức đặc sản như cam Vinh v&agrave; ch&aacute;o lươn v&agrave; tham quan c&aacute;c điểm du lịch hấp dẫn trong th&agrave;nh phố như: Th&agrave;nh cổ Nghệ An, L&acirc;m vi&ecirc;n N&uacute;i Quyết, ch&ugrave;a Cần Linh, đền Hồng Sơn, chợ Vinh, c&aacute;c bảo t&agrave;ng (bảo t&agrave;ng tổng hợp Nghệ An, bảo t&agrave;ng X&ocirc; Viết Nghệ Tĩnh, bảo t&agrave;ng Qu&acirc;n khu IV), c&ocirc;ng vi&ecirc;n (c&ocirc;ng vi&ecirc;n Hồ Cửa Nam, c&ocirc;ng vi&ecirc;n Nguyễn Tất Th&agrave;nh, c&ocirc;ng vi&ecirc;n trung t&acirc;m th&agrave;nh phố với tượng đ&agrave;i B&aacute;c Hồ v&agrave; Quảng trường Hồ Ch&iacute; Minh)...</p>\r\n\r\n<p>&nbsp;Du kh&aacute;ch c&oacute; thể tham gia lễ hội ở Vinh như: lễ hội đền Hồng Sơn, tổ chức v&agrave;o ng&agrave;y 3 th&aacute;ng 3 v&agrave; ng&agrave;y 20 th&aacute;ng 8 &acirc;m lịch...</p>\r\n\r\n<h2><strong>C&Aacute;CH T&Iacute;NH GI&Aacute; V&Agrave; PH&Aacute;T SINH</strong></h2>\r\n\r\n<table border=\"3\" cellspacing=\"3\" style=\"width:536px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Taxi H&agrave; Nội - Vinh</td>\r\n			<td>\r\n			<p>Gi&aacute; Tiền</p>\r\n\r\n			<p>(Xe Ri&ecirc;ng, Trọn G&oacute;i)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xe 4 Chỗ</td>\r\n			<td>2.400.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xe 5 Chỗ</td>\r\n			<td>2.670.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xe 7 Chỗ</td>\r\n			<td>3.260.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xe 16 Chỗ</td>\r\n			<td>4.450.000đ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><br />\r\n<strong>Ch&uacute; &yacute;:</strong></p>\r\n\r\n<p>- Bảng gi&aacute; tr&ecirc;n chưa bao gồm ph&iacute; cầu đường.</p>\r\n\r\n<p>- Gi&aacute; tr&ecirc;n sẽ được&nbsp;<strong>miễn ph&iacute;</strong>&nbsp;&quot;ph&iacute; cầu đường&quot; nếu qu&yacute; kh&aacute;ch khởi h&agrave;nh từ 6h -18h v&agrave; đặt xe trước 6 tiếng.&nbsp;</p>\r\n\r\n<p>- Xe 4 chỗ: 8000đ/km ph&aacute;t sinh.</p>\r\n\r\n<p>- Xe 5 chỗ: 9000đ/km ph&aacute;t sinh.</p>\r\n\r\n<p>- Xe 7 chỗ: 11.000đ/km ph&aacute;t sinh.&nbsp;&nbsp;</p>\r\n\r\n<h2><em>&nbsp;</em><strong>TAXIGO CAM KẾT&nbsp;</strong></h2>\r\n\r\n<p><em>-</em>&nbsp;Ch&uacute;ng t&ocirc;i đặt chất lượng dịch vụ l&ecirc;n h&agrave;ng đầu, với 100% d&ograve;ng xe sedan sang trọng, xe mới, sạch sẽ, l&aacute;i xe th&acirc;n thiện, lịch sự v&agrave; đ&uacute;ng giờ.</p>\r\n\r\n<p>- Ch&uacute;ng t&ocirc;i sẽ kết nối kh&aacute;ch h&agrave;ng với