CREATE TABLE `notice_statics` (
  `id` int(11) NOT NULL,
  `title` varchar(255) CHARACTER SET utf8 NOT NULL,
  `descriptionSeo` text CHARACTER SET utf8 NOT NULL,
  `keywordSeo` varchar(255) CHARACTER SET utf8 NOT NULL,
  `alt` varchar(255) CHARACTER SET utf8 NOT NULL,
  `lock` int(1) NOT NULL,
  `status` int(1) NOT NULL,
  `slug` varchar(255) CHARACTER SET utf16 NOT NULL,
  `image` varchar(125) CHARACTER SET utf8 DEFAULT NULL,
  `author` varchar(50) CHARACTER SET utf8 DEFAULT NULL,
  `create_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `content` longtext CHARACTER SET utf32,
  `description` text CHARACTER SET utf8,
  `views` int(11) NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=latin1;

--
-- Đang đổ dữ liệu cho bảng `notice_statics`
--

INSERT INTO `notice_statics` (`id`, `title`, `descriptionSeo`, `keywordSeo`, `alt`, `lock`, `status`, `slug`, `image`, `author`, `create_at`, `content`, `description`, `views`) VALUES
(1, 'Điều khoản xử dụng', '', '', '121 điều khoản xử dụng', 0, 0, '121-dieu-khoan-xu-dung', '/app/webroot/upload/images/474f4cdd4383ff91fd1d98bcb039d93b_L.jpg', 'Vũ Dương', '2018-01-22 13:47:55', '1. CHẤP NHẬN C&Aacute;C ĐIỀU KHOẢN\r\n<ul>\r\n	<li>Thỏa thuận sau đ&acirc;y l&agrave; một thỏa thuận r&agrave;ng buộc ph&aacute;p l&yacute; giữa Bạn&nbsp;v&agrave; TaxiGo.Vn (&quot;Ch&uacute;ng t&ocirc;i&rdquo;). Thỏa thuận bao gồm c&aacute;c điều khoản dịch vụ m&agrave; bạn phải tu&acirc;n thủ để được quyền tiếp cận v&agrave; sử dụng dịch vụ&nbsp; tr&ecirc;n trang Web v&agrave; c&aacute;c ứng dụng di đ&ocirc;̣ng của ch&uacute;ng t&ocirc;i. Tại từng thời điểm, ch&uacute;ng t&ocirc;i c&oacute; thể cập nhật Điều khoản dịch vụ theo quyết định của m&igrave;nh m&agrave; kh&ocirc;ng cần th&ocirc;ng b&aacute;o trực tiếp cho bạn. Bạn chịu tr&aacute;ch nhiệm t&igrave;m hiểu phi&ecirc;n bản mới nhất bằng c&aacute;ch truy cập website www.tienchuyen.vn</li>\r\n	<li>XIN VUI L&Ograve;NG ĐỌC ĐIỀU KHOẢN DỊCH VỤ N&Agrave;Y MỘT C&Aacute;CH CẨN TRỌNG TRƯỚC KHI ĐĂNG K&Yacute; L&Agrave;M TH&Agrave;NH VI&Ecirc;N. NẾU BẠN KH&Ocirc;NG ĐỒNG &Yacute; VỚI NHỮNG R&Agrave;NG BUỘC CỦA ĐIỀU KHOẢN DỊCH VỤ N&Agrave;Y, VUI L&Ograve;NG KH&Ocirc;NG SỬ DỤNG HOẶC TRUY CẬP DỊCH VỤ CỦA CH&Uacute;NG T&Ocirc;I.</li>\r\n</ul>\r\n2. TR&Aacute;CH NHIỆM VỚI T&Agrave;I KHOẢN, MẬT KHẨU ĐƯỢC CUNG CẤP\r\n\r\n<ul>\r\n	<li>Bạn sẽ nhận được một mật khẩu v&agrave; t&agrave;i khoản sau khi ho&agrave;n tất thủ tục đăng k&yacute; l&agrave;m th&agrave;nh vi&ecirc;n. Bạn c&oacute; tr&aacute;ch nhiệm bảo mật mật khẩu v&agrave; t&agrave;i khoản, v&agrave; ho&agrave;n to&agrave;n chịu tr&aacute;ch nhiệm đối với tất cả c&aacute;c hoạt động diễn ra dưới việc sử dụng mật khẩu hoặc t&agrave;i khoản của m&igrave;nh. Bạn đồng &yacute; (a) th&ocirc;ng b&aacute;o ngay cho ch&uacute;ng t&ocirc;i về bất cứ trường hợp n&agrave;o sử dụng tr&aacute;i ph&eacute;p mật khẩu hoặc t&agrave;i khoản của bạn hoặc bất cứ trường hợp n&agrave;o kh&aacute;c vi phạm sự bảo mật, v&agrave; (b) bảo đảm rằng Bạn lu&ocirc;n tho&aacute;t khỏi t&agrave;i khoản của m&igrave;nh sau mỗi lần sử dụng. Ch&uacute;ng t&ocirc;i kh&ocirc;ng thể v&agrave; sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm đối với bất cứ tổn thất hoặc thiệt hại n&agrave;o ph&aacute;t sinh do Bạn kh&ocirc;ng tu&acirc;n thủ quy định n&agrave;y.</li>\r\n</ul>\r\n3. TR&Aacute;CH NHIỆM CUNG CẤP TH&Ocirc;NG TIN\r\n\r\n<ul>\r\n	<li>Với tư c&aacute;ch l&agrave; người sử dụng dịch vụ, Bạn phải cam kết:\r\n	<ul>\r\n		<li>(a) bạn c&oacute; đủ tư c&aacute;ch ph&aacute;p l&yacute; v&agrave; tư c&aacute;ch h&agrave;nh vi khi sử dụng dịch vụ</li>\r\n		<li>(b) cung cấp c&aacute;c th&ocirc;ng tin đầy đủ, trung thực v&agrave; ch&iacute;nh x&aacute;c về bản th&acirc;n trong Hồ sơ c&aacute; nh&acirc;n</li>\r\n		<li>(c) duy tr&igrave; v&agrave; cập nhật kịp thời Hồ sơ c&aacute; nh&acirc;n để bảo đảm rằng dữ liệu n&agrave;y l&agrave; đầy đủ, trung thực v&agrave; ch&iacute;nh x&aacute;c.</li>\r\n	</ul>\r\n	</li>\r\n	<li>Nếu Bạn cung cấp bất cứ th&ocirc;ng tin n&agrave;o kh&ocirc;ng phải l&agrave; kh&ocirc;ng trung thực hoặc kh&ocirc;ng ch&iacute;nh x&aacute;c, hoặc nếu ch&uacute;ng t&ocirc;i c&oacute; căn cứ hợp l&yacute; để nghi ngờ rằng th&ocirc;ng tin đ&oacute; kh&ocirc;ng trung thực hoặc kh&ocirc;ng ch&iacute;nh x&aacute;c, ch&uacute;ng t&ocirc;i c&oacute; quyền đ&igrave;nh chỉ hoặc chấm dứt t&agrave;i khoản của Bạn; từ chối một phần hoặc to&agrave;n bộ việc sử dụng Dịch vụ tại thời điểm hiện tại hoặc trong tương lai.</li>\r\n</ul>\r\n4. TR&Aacute;CH NHIỆM NỘI DUNG ĐĂNG TẢI\r\n\r\n<ul>\r\n	<li>Bạn hiểu rằng bạn ho&agrave;n to&agrave;n chịu tr&aacute;ch nhiệm trước ph&aacute;p luật về c&aacute;c Nội dung bạn tạo ra bao gồm nhưng kh&ocirc;ng giới hạn ở dữ liệu, văn bản, phần mềm, &acirc;m thanh, h&igrave;nh ảnh, video, email. Ch&uacute;ng t&ocirc;i kh&ocirc;ng c&oacute; nghĩa vụ theo d&otilde;i c&aacute;c t&agrave;i liệu được đăng v&agrave; v&igrave; vậy, kh&ocirc;ng bảo đảm về t&iacute;nh hợp ph&aacute;p, ch&iacute;nh x&aacute;c, nguy&ecirc;n vẹn hoặc chất lượng của Nội Dung đ&oacute;. Tại c&aacute;c khu vực được ph&eacute;p đăng tải Nội dung, bạn c&oacute; thể chia sẻ th&ocirc;ng tin được ph&eacute;p dưới c&aacute;c định dạng ch&uacute;ng t&ocirc;i mặc định v&agrave; bạn phải tự chịu tr&aacute;ch nhiệm đối với c&aacute;c nội dung, th&ocirc;ng tin, h&igrave;nh ảnh v&agrave; bất kỳ sự chia sẻ n&agrave;o kh&aacute;c của bạn với c&aacute; nh&acirc;n người sử dụng hoặc nh&oacute;m người sử dụng. Tuy nhi&ecirc;n, trong mọi trường hợp, ch&uacute;ng t&ocirc;i vẫn được bảo lưu quyền xử l&yacute; c&aacute;c th&ocirc;ng tin đăng tải cho ph&ugrave; hợp với thuần phong mỹ tục, c&aacute;c quy tắc đạo đức v&agrave; c&aacute;c quy tắc đảm bảo an ninh quốc gia, v&agrave; ch&uacute;ng t&ocirc;i c&oacute; to&agrave;n quyền cho ph&eacute;p hoặc kh&ocirc;ng cho ph&eacute;p b&agrave;i viết của bạn xuất hiện hay tồn tại tại c&aacute;c khu vực được ph&eacute;p chia sẻ th&ocirc;ng tin.</li>\r\n</ul>\r\n5. HẠN CHẾ HOẠT ĐỘNG\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng giới hạn c&aacute;c biện ph&aacute;p khắc phục hậu quả kh&aacute;c, ch&uacute;ng t&ocirc;i c&oacute; thể chấm dứt sự tham gia của bạn, loại bỏ th&ocirc;ng tin của bạn&nbsp; v&agrave; từ chối cung cấp dịch vụ nếu:\r\n	<ul>\r\n		<li>(a).&nbsp;Bạn đ&atilde; sử dụng sai mục đ&iacute;ch c&aacute;c c&aacute;c dịch vụ, c&oacute; h&agrave;nh vi tr&aacute;i ph&aacute;p luật hoặc đạo đức.</li>\r\n		<li>(b).&nbsp;ch&uacute;ng t&ocirc;i c&oacute; căn cứ hợp l&yacute; để nghi ngờ rằng th&ocirc;ng tin bạn cung cấp kh&ocirc;ng trung thực hoặc kh&ocirc;ng ch&iacute;nh x&aacute;c.</li>\r\n		<li>(c). ch&uacute;ng t&ocirc;i tin rằng h&agrave;nh động của bạn l&agrave; gian lận, lừa đảo v&agrave; c&oacute; thể g&acirc;y ra tổn thất t&agrave;i ch&iacute;nh hoặc tr&aacute;ch nhiệm ph&aacute;p l&yacute; cho&nbsp; ch&uacute;ng t&ocirc;i.</li>\r\n		<li>(d). sử dụng c&aacute;c dịch vụ với mục đ&iacute;ch thương mại m&agrave; kh&ocirc;ng c&oacute; sự đồng &yacute; trước bằng văn bản đồng &yacute; của ch&uacute;ng t&ocirc;i.</li>\r\n		<li>(e). vi phạm c&aacute;c quyền của ch&uacute;ng t&ocirc;i hoặc b&ecirc;n thứ ba, bao gồm nhưng kh&ocirc;ng giới hạn quyền sở hữu tr&iacute; tuệ, quyền t&aacute;c giả, bằng s&aacute;ng chế, thương hiệu, b&iacute; mật thương mại hoặc c&aacute;c quyền ri&ecirc;ng tư.</li>\r\n		<li>(f). ph&aacute;t t&aacute;n c&aacute;c ứng dụng độc hại hoặc tấn c&ocirc;ng chiếm đoạt quyền điều khiển hệ thống, dữ liệu hoặc th&ocirc;ng tin c&aacute; nh&acirc;n.</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n6. BẢO MẬT\r\n\r\n<ul>\r\n	<li>Ch&uacute;ng t&ocirc;i kh&ocirc;ng b&aacute;n hoặc cho thu&ecirc; th&ocirc;ng tin của bạn cho b&ecirc;n thứ ba m&agrave; kh&ocirc;ng c&oacute; sự cho ph&eacute;p của bạn v&agrave; ch&uacute;ng t&ocirc;i chỉ sử dụng th&ocirc;ng tin của bạn như được m&ocirc; tả trong</li>\r\n	<li>Ch&uacute;ng t&ocirc;i xem bảo vệ sự ri&ecirc;ng tư của người sử dụng như một nguy&ecirc;n tắc quan trọng h&agrave;ng đầu. Bạn c&oacute; thể xem x&eacute;t lại ch&iacute;nh s&aacute;ch bảo mật hiện tại của ch&uacute;ng t&ocirc;i tại</li>\r\n</ul>\r\n7. KHIẾU NẠI V&Agrave; GIẢI QUYẾT TRANH CHẤP\r\n\r\n<ul>\r\n	<li>Th&ocirc;ng qua việc đăng k&yacute; hoặc sử dụng website v&agrave; dịch vụ của ch&uacute;ng t&ocirc;i, bạn mặc nhi&ecirc;n đồng &yacute; v&agrave; tu&acirc;n thủ to&agrave;n bộ c&aacute;c quy định của Luật ph&aacute;p Việt Nam . Trong trường hợp một hoặc một số điều khoản của bản Quy định sử dụng n&agrave;y xung đột với c&aacute;c quy định của luật ph&aacute;p v&agrave; bị T&ograve;a &aacute;n coi l&agrave; v&ocirc; hiệu, điều khoản đ&oacute; sẽ được chỉnh sửa cho ph&ugrave; hợp với quy định của ph&aacute;p luật hiện h&agrave;nh, v&agrave; phần c&ograve;n lại của Quy định sử dụng vẫn giữ nguy&ecirc;n gi&aacute; trị.</li>\r\n</ul>\r\n', 'abc', 7),
(6, 'Taxi Hà Nội về Nam Định giá cực rẻ chỉ với 680.000.đ - TaxiGo.vn', 'Taxi Hà Nội Nam Định giá tốt nhất chỉ với 680.000.đ - TaxiGo.vn. Dịch vụ Taxi Hà Nội Nam Định chuyên nghiệp, đặt xe nhanh chóng, uy tín và tin cậy nhất.', '', '27-taxi-ha-noi-ve-nam-dinh', 0, 0, '27-taxi-ha-noi-ve-nam-dinh', '/app/webroot/upload/files/taxi-ha-noi-nam-dinh.jpg', '', '2018-01-29 09:02:56', '<p><strong>Dịch vụ Taxi H&agrave; Nội về Nam Định</strong></p>\r\n\r\n<p><strong>Ở b&agrave;i viết n&agrave;y, TaxiGo giới thiệu tới qu&yacute; kh&aacute;ch h&agrave;ng dịch vụ&nbsp;thu&ecirc; xe đường d&agrave;i gi&aacute; rẻ. H&igrave;nh thức thu&ecirc;, c&aacute;ch t&iacute;nh gi&aacute; cả v&agrave; ph&aacute;t sinh giữa H&agrave; Nội v&agrave; Nam Định.</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Taxi H&agrave; Nội - Nam Định: 680.000đ</strong></p>\r\n	</li>\r\n	<li><strong>Giới hạn tối đa 90km, từ Trung t&acirc;m HN về trung t&acirc;m TP.Nam Định l&agrave; 85km</strong></li>\r\n</ul>\r\n\r\n<table align=\"center\" border=\"3\" cellspacing=\"3\" style=\"width:400px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\">Taxi H&agrave; Nội - Nam Định</td>\r\n			<td>\r\n			<p style=\"text-align:center\">Gi&aacute; Tiền</p>\r\n\r\n			<p style=\"text-align:center\">(Xe Ri&ecirc;ng, Trọn G&oacute;i)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Xe 4 Chỗ</td>\r\n			<td style=\"text-align:center\">680.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Xe 5 Chỗ</td>\r\n			<td style=\"text-align:center\">770.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Xe 7 Chỗ</td>\r\n			<td style=\"text-align:center\">940.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Xe 16 Chỗ</td>\r\n			<td style=\"text-align:center\">1.280.000đ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>Ch&uacute; &yacute;:&nbsp;</strong></p>\r\n\r\n<p>+ Gi&aacute; tr&ecirc;n chưa bao gồm ph&iacute; cầu đường. (Tuỳ từng khung giờ đặt xe v&agrave; cung đường sẽ được miễn ph&iacute; ph&iacute; cầu đường)</p>\r\n\r\n<p>+ Gi&aacute; tr&ecirc;n kh&ocirc;ng &aacute;p dụng cho ng&agrave;y nghĩ lễ, tết. Gi&aacute; chưa bao gồm ph&iacute; cầu đường v&agrave; thuế VAT.</p>\r\n\r\n<p>+ Lu&ocirc;n cam kết 100% xe mới, chất lượng phục vụ tốt với phương ch&acirc;m &ldquo;An To&agrave;n - Sang Trọng - Tiết Kiệm&rdquo;</p>\r\n\r\n<p><strong>1/ Chất lượng dịch vụ:</strong><br />\r\n&nbsp; &nbsp; - TaxiGo cam kết đ&oacute;n kh&aacute;ch đ&uacute;ng giờ, l&aacute;i xe kinh nghiệm, lịch sự, th&acirc;n thiện.<br />\r\n&nbsp; &nbsp; - Cam kết 100% l&agrave; xe mới, sạch sẽ.<br />\r\n&nbsp; &nbsp; - Trước giờ khởi h&agrave;nh 1 tiếng ch&uacute;ng t&ocirc;i sẽ th&ocirc;ng b&aacute;o cụ thể t&ecirc;n l&aacute;i xe, số điện thoại, biển số v&agrave; loại xe sẽ đ&oacute;n qu&yacute; kh&aacute;ch.<br />\r\n&nbsp; &nbsp; - Ho&agrave;n lại 20%-200% tiền cho qu&yacute; kh&aacute;ch nếu qu&yacute; kh&aacute;ch kh&ocirc;ng h&agrave;i l&ograve;ng về dịch vụ của ch&uacute;ng t&ocirc;i.<br />\r\n<strong>2/ H&igrave;nh thức đặt xe:</strong><br />\r\n&nbsp; &nbsp; - Q&uacute;y kh&aacute;ch n&ecirc;n li&ecirc;n hệ đặt xe trước giờ khởi h&agrave;nh 3 tiếng để c&oacute; gi&aacute; tốt nhất.<br />\r\n&nbsp; &nbsp; - Nếu qu&yacute; kh&aacute;ch đặt xe từ tỉnh về H&agrave; Nội vui l&ograve;ng b&aacute;o trước 6 tiếng, t&ugrave;y trường hợp sẽ phải đặt trước 30% bằng c&aacute;ch chuyển khoản.<br />\r\n&nbsp; &nbsp; - Để đảm bảo qu&yacute; kh&aacute;ch sẽ kh&ocirc;ng phải trả th&ecirc;m bất kỳ khoản chi ph&iacute; n&agrave;o cho L&aacute;i xe, Ch&uacute;ng t&ocirc;i sẽ b&aacute;o lại chi tiết mức giảm gi&aacute; th&ecirc;m, hoạc chi ph&iacute; ph&aacute;t sinh (nếu c&oacute;) cho qu&yacute; kh&aacute;ch trước giờ khởi h&agrave;nh 3-5 tiếng,<br />\r\n<strong>3/ C&aacute;ch t&iacute;nh gi&aacute; v&agrave; ph&aacute;t sinh:</strong><br />\r\nHiện tại ch&uacute;ng t&ocirc;i đang c&oacute; mức gi&aacute; cố định giữa hai th&agrave;nh phố như sau:</p>\r\n\r\n<p><strong>&nbsp;Taxi&nbsp;H&agrave; Nội - Nam Định: 680.000đ</strong></p>\r\n\r\n<p>+ Xe 4 chỗ: 8000đ/km ph&aacute;t sinh.</p>\r\n\r\n<p>+ Xe 5 chỗ: 9000đ/km ph&aacute;t sinh.</p>\r\n\r\n<p>+ Xe 7 chỗ: 11.000đ/km ph&aacute;t sinh.</p>\r\n\r\n<p>+ Xe 16 chỗ: 15.000d/km ph&aacute;t sinh.&nbsp;</p>\r\n', '', 17),
(30, 'Taxi Hà Nội - Ninh Bình giá sốc chỉ còn 760.000đ - TaxiGo.vn', 'Taxi Hà Nội Ninh Bình giá sốc chỉ còn 760.000đ TaxiGo.vn. TaxiGo cam kết đón khách đi tuyến Taxi Hà Nội Ninh Bình đúng giờ, lái xe kinh nghiệm, lịch sự, thân thiện.', '', '26-taxi-ha-noi-di-ninh-binh', 0, 0, '26-taxi-ha-noi-di-ninh-binh', '/app/webroot/upload/files/feb4274796d93ff716e9650163a77fb8_L.jpg', '', '2018-02-21 15:58:35', '<p><strong>Ở b&agrave;i viết n&agrave;y, TaxiGo giới thiệu tới qu&yacute; kh&aacute;ch h&agrave;ng dịch vụ&nbsp;Taxi đường d&agrave;i gi&aacute; rẻ. H&igrave;nh thức thu&ecirc;, c&aacute;ch t&iacute;nh gi&aacute; cả v&agrave; ph&aacute;t sinh giữa H&agrave; Nội v&agrave; TP.Ninh B&igrave;nh.</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Taxi từ H&agrave; Nội &ndash; TP. Ninh B&igrave;nh: 760.000đ</strong></p>\r\n	</li>\r\n	<li><strong>Giới hạn tối đa 100km, từ Trung t&acirc;m H&agrave; Nội về trung t&acirc;m TP.Ninh B&igrave;nh l&agrave; 95km</strong></li>\r\n</ul>\r\n\r\n<table align=\"center\" border=\"3\" cellspacing=\"3\" style=\"width:400px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\">Taxi H&agrave; Nội - Ninh B&igrave;nh</td>\r\n			<td>\r\n			<p style=\"text-align:center\">Gi&aacute; Tiền</p>\r\n\r\n			<p style=\"text-align:center\">(Xe Ri&ecirc;ng, Trọn G&oacute;i)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Xe 4 Chỗ</td>\r\n			<td style=\"text-align:center\">760.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Xe 5 Chỗ</td>\r\n			<td style=\"text-align:center\">860.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Xe 7 Chỗ</td>\r\n			<td style=\"text-align:center\">1.045.000đ</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Xe 16 Chỗ</td>\r\n			<td style=\"text-align:center\">1.430.000đ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>Ch&uacute; &yacute;:&nbsp;</strong></p>\r\n\r\n<p>+ Gi&aacute; tr&ecirc;n chưa bao gồm ph&iacute; cầu đường. (Tuỳ 